import React, { useState } from 'react';
import Product from './components/Product';
import './App.css';

const productData = Array.from({ length: 20 }, (_, i) => ({
    id: i + 1,
    name: `Product ${i + 1}`,
    category: i % 2 === 0 ? 'Clothing' : 'Electronics',
    price: (Math.random() * 100).toFixed(2),
    image: 'https://via.placeholder.com/300x200?text=Product',
}));

function App() {
    const [cartItems, setCartItems] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('');

    const addToCart = (product) => setCartItems([...cartItems, product]);

    const filteredProducts = productData.filter(
        (product) =>
            product.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
            (selectedCategory === '' || product.category === selectedCategory)
    );

    return (
        <div className="App">
            <header className="navbar">
                <h1 className="logo">My Store</h1>
                <input
                    type="text"
                    placeholder="Search products..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-bar"
                />
                <div className="cart">ðŸ›’ Cart ({cartItems.length})</div>
            </header>

            <div className="content">
                <aside className="sidebar">
                    <h2>Categories</h2>
                    <ul>
                        <li onClick={() => setSelectedCategory('')}>All</li>
                        <li onClick={() => setSelectedCategory('Clothing')}>Clothing</li>
                        <li onClick={() => setSelectedCategory('Electronics')}>Electronics</li>
                    </ul>
                </aside>

                <main className="product-grid">
                    {filteredProducts.map((product) => (
                        <Product key={product.id} product={product} addToCart={addToCart} />
                    ))}
                </main>
            </div>

            <footer className="footer">
                <p>&copy; 2024 My Store. All rights reserved.</p>
            </footer>
        </div>
    );
}

export default App;
