import React, { useState } from 'react';
import './App.css'; // Import styles

const products = [
    {
        id: 1,
        name: 'Nike Air Max',
        img: 'https://images.unsplash.com/photo-1584862955159-f032a568e677',
        price: 120,
    },
    {
        id: 2,
        name: 'Apple Watch Series 8',
        img: 'https://images.unsplash.com/photo-1605792657660-c3720e3b12d3',
        price: 400,
    },
    {
        id: 3,
        name: 'Adidas Ultraboost',
        img: 'https://images.unsplash.com/photo-1598188300297-f8a92407a248',
        price: 180,
    },
    {
        id: 4,
        name: 'The North Face Jacket',
        img: 'https://images.unsplash.com/photo-1618354694551-3e61f8b5c05a',
        price: 220,
    },
    {
        id: 5,
        name: 'Sony WH-1000XM4',
        img: 'https://images.unsplash.com/photo-1580200054512-9e7f89c5c69f',
        price: 350,
    },
    {
        id: 6,
        name: 'AirPods Pro',
        img: 'https://images.unsplash.com/photo-1606290696793-3c9e9f8a51c7',
        price: 250,
    },
];

function App() {
    const [cartItems, setCartItems] = useState([]);
    const [cartOpen, setCartOpen] = useState(false);

    const toggleCart = () => setCartOpen(!cartOpen);

    const addToCart = (product) => {
        setCartItems((prevItems) => [...prevItems, product]);
    };

    return (
        <div className="App">
            {/* Header */}
            <header className="navbar">
                <div className="logo">Aman's Store</div>
                <div className="cart-container" onClick={toggleCart}>
                    <span className="cart-icon">ðŸ›’ ({cartItems.length})</span>
                    {cartOpen && (
                        <div className="cart-dropdown">
                            {cartItems.length === 0 ? (
                                <p>Your cart is empty.</p>
                            ) : (
                                <ul>
                                    {cartItems.map((item, index) => (
                                        <li key={index}>
                                            {item.name} - ${item.price}
                                        </li>
                                    ))}
                                </ul>
                            )}
                        </div>
                    )}
                </div>
            </header>

            {/* Main Content */}
            <main className="content">
                <div className="product-grid">
                    {products.map((product) => (
                        <div className="product-card" key={product.id}>
                            <img src={product.img} alt={product.name} />
                            <h3>{product.name}</h3>
                            <p>${product.price}</p>
                            <button onClick={() => addToCart(product)}>Add to Cart</button>
                        </div>
                    ))}
                </div>
            </main>

            {/* Footer */}
            <footer className="footer">Â© 2024 Aman's Store. All rights reserved.</footer>
        </div>
    );
}

export default App;
