import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import Product from './components/Product';
import Cart from './components/Cart';
import './App.css';

const productData = [
    { id: 1, name: 'Nike Shoes', category: 'Clothing', price: 89.99, image: 'https://via.placeholder.com/300x200?text=Nike+Shoes' },
    { id: 2, name: 'Adidas Jacket', category: 'Clothing', price: 120.49, image: 'https://via.placeholder.com/300x200?text=Adidas+Jacket' },
    { id: 3, name: 'Apple Watch', category: 'Electronics', price: 199.99, image: 'https://via.placeholder.com/300x200?text=Apple+Watch' },
];

function App() {
    const [cartItems, setCartItems] = useState([]);

    const addToCart = (product) => setCartItems([...cartItems, product]);

    const removeFromCart = (productToRemove) =>
        setCartItems(cartItems.filter((item) => item.id !== productToRemove.id));

    return (
        <Router>
            <div className="App">
                <header className="navbar">
                    <h1 className="logo">My Store</h1>
                    <Link to="/cart" className="cart">
                        ðŸ›’ Cart ({cartItems.length})
                    </Link>
                </header>

                <Routes>
                    <Route
                        path="/"
                        element={
                            <div className="content">
                                <main className="product-grid">
                                    {productData.map((product) => (
                                        <Product key={product.id} product={product} addToCart={addToCart} />
                                    ))}
                                </main>
                            </div>
                        }
                    />
                    <Route
                        path="/cart"
                        element={<Cart cartItems={cartItems} removeFromCart={removeFromCart} />}
                    />
                </Routes>

                <footer className="footer">
                    <p>&copy; 2024 My Store. All rights reserved.</p>
                </footer>
            </div>
        </Router>
    );
}

export default App;
