// src/App.js
import React, { useState } from 'react';
import Product from './components/Product';
import Cart from './components/Cart';
import './App.css';

const productData = [
    { id: 1, name: 'Nike Shoes', price: 89.99, image: 'https://via.placeholder.com/300x200?text=Nike+Shoes' },
    { id: 2, name: 'Adidas Jacket', price: 120.49, image: 'https://via.placeholder.com/300x200?text=Adidas+Jacket' },
    { id: 3, name: 'Apple Watch', price: 199.99, image: 'https://via.placeholder.com/300x200?text=Apple+Watch' },
];

function App() {
    const [cartItems, setCartItems] = useState([]);

    const addToCart = (product) => {
        if (!cartItems.includes(product)) {
            setCartItems([...cartItems, product]);
        } else {
            alert(`${product.name} is already in the cart.`);
        }
    };

    const removeFromCart = (product) => {
        setCartItems(cartItems.filter((item) => item !== product));
    };

    return (
        <div className="App">
            <header className="navbar">
                <h1 className="logo">My Store</h1>
                <div className="cart-icon">
                    ðŸ›’ <span>{cartItems.length}</span>
                </div>
            </header>

            <div className="content">
                <aside className="sidebar">
                    <h2>Categories</h2>
                    <ul>
                        <li>Electronics</li>
                        <li>Clothing</li>
                        <li>Home & Garden</li>
                        <li>More...</li>
                    </ul>
                </aside>

                <main className="product-list">
                    {productData.map((product) => (
                        <Product key={product.id} product={product} addToCart={addToCart} />
                    ))}
                </main>
            </div>

            <footer className="footer">
                <p>&copy; 2024 My Store. All rights reserved.</p>
            </footer>
        </div>
    );
}

export default App;
