import React, { useState } from 'react';
import Product from './components/Product';
import './App.css';

const productData = [
    { id: 1, name: 'Nike Shoes', category: 'Clothing', price: 89.99, image: 'https://via.placeholder.com/300x200?text=Nike+Shoes' },
    { id: 2, name: 'Adidas Jacket', category: 'Clothing', price: 120.49, image: 'https://via.placeholder.com/300x200?text=Adidas+Jacket' },
    { id: 3, name: 'Apple Watch', category: 'Electronics', price: 199.99, image: 'https://via.placeholder.com/300x200?text=Apple+Watch' },
    { id: 4, name: 'Laptop', category: 'Electronics', price: 499.99, image: 'https://via.placeholder.com/300x200?text=Laptop' },
];

function App() {
    const [cartItems, setCartItems] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('');

    const addToCart = (product) => setCartItems([...cartItems, product]);

    const filteredProducts = productData.filter(
        (product) =>
            product.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
            (selectedCategory === '' || product.category === selectedCategory)
    );

    return (
        <div className="App">
            <header className="navbar">
                <h1 className="logo">My Store</h1>
                <input
                    type="text"
                    placeholder="Search products..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-bar"
                />
                <div className="cart">
                    ðŸ›’ Cart ({cartItems.length})
                </div>
            </header>

            <div className="content">
                <aside className="sidebar">
                    <h2>Categories</h2>
                    <ul>
                        <li onClick={() => setSelectedCategory('')}>All</li>
                        <li onClick={() => setSelectedCategory('Clothing')}>Clothing</li>
                        <li onClick={() => setSelectedCategory('Electronics')}>Electronics</li>
                    </ul>
                </aside>

                <main className="product-list">
                    {filteredProducts.map((product) => (
                        <Product key={product.id} product={product} addToCart={addToCart} />
                    ))}
                </main>
            </div>

            <footer className="footer">
                <p>&copy; 2024 My Store. All rights reserved.</p>
            </footer>
        </div>
    );
}

export default App;
