import React from 'react';
import './Product.css';

function Product({ product, addToCart }) {
    const onSale = product.id % 2 === 0;
    const discountedPrice = (product.price * 0.85).toFixed(2);

    return (
        <div className="product-card">
            {onSale && <div className="sale-badge">Sale</div>}
            <img src={product.image} alt={product.name} />
            <h3>{product.name}</h3>

            {onSale ? (
                <div className="price-section">
                    <p className="old-price">${product.price.toFixed(2)}</p>
                    <p className="new-price">${discountedPrice}</p>
                </div>
            ) : (
                <p className="price">${product.price.toFixed(2)}</p>
            )}

            <button onClick={() => addToCart(product)}>Add to Cart</button>
        </div>
    );
}

export default Product;
