import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from 'react-router-dom';
import Product from './components/Product';
import './App.css';
import CartReview from './components/CartReview';

const productData = [
    {
        id: 1,
        name: 'North Face Jacket',
        category: 'Clothing',
        price: 99.99,
        image: 'https://cdn11.bigcommerce.com/s-9p889rxpkb/images/stencil/1280x1280/products/201/883/TheNorthFaceJacket-1__65715.1641366741.jpg?c=2',
    },
    {
        id: 2,
        name: 'Nike Air Max 270',
        category: 'Clothing',
        price: 120.0,
        image: 'https://static.nike.com/a/images/t_PDP_1280_v1/f_auto,q_auto:eco/3a87f1a6-4491-46a7-9640-71dfe8dfbb81/air-max-270-mens-shoes-KkLcGR.png',
    },
    {
        id: 3,
        name: 'Sony WH-1000XM4',
        category: 'Electronics',
        price: 299.99,
        image: 'https://m.media-amazon.com/images/I/61D4Z3yKPAL._AC_SL1500_.jpg',
    },
];

function App() {
    const [cartItems, setCartItems] = useState([]);
    const [cartOpen, setCartOpen] = useState(false);
    const navigate = useNavigate();

    const addToCart = (product) => setCartItems([...cartItems, product]);

    const removeFromCart = (productToRemove) =>
        setCartItems(cartItems.filter((item) => item.id !== productToRemove.id));

    const toggleCart = () => setCartOpen(!cartOpen);

    const handleReviewCart = () => {
        setCartOpen(false); // Close the cart dropdown
        navigate('/cart-review'); // Navigate to the cart review page
    };

    return (
        <div className="App">
            <header className="navbar">
                <h1 className="logo">My Store</h1>
                <div className="cart" onClick={toggleCart}>
                    ðŸ›’ Cart ({cartItems.length})
                </div>
            </header>

            {cartOpen && (
                <div className="cart-dropdown">
                    {cartItems.length > 0 ? (
                        <>
                            <ul>
                                {cartItems.map((item, index) => (
                                    <li key={index}>
                                        <span>{item.name} - ${item.price}</span>
                                        <button onClick={() => removeFromCart(item)}>Remove</button>
                                    </li>
                                ))}
                            </ul>
                            <button onClick={handleReviewCart}>Review Cart</button>
                        </>
                    ) : (
                        <p>Your cart is empty.</p>
                    )}
                </div>
            )}

            <div className="content">
                <main className="product-grid">
                    {productData.map((product) => (
                        <Product key={product.id} product={product} addToCart={addToCart} />
                    ))}
                </main>
            </div>

            <footer className="footer">
                <p>&copy; 2024 My Store. All rights reserved.</p>
            </footer>
        </div>
    );
}

export default App;
