import React, { useState } from 'react';
import Product from './components/Product';
import './App.css';

const productData = [
    {
        id: 1,
        name: 'North Face Jacket',
        category: 'Clothing',
        price: 99.99,
        image: process.env.PUBLIC_URL + '/assets/north-face-jacket.jpg',
    },
    {
        id: 2,
        name: 'Nike Air Max',
        category: 'Clothing',
        price: 120.0,
        image: process.env.PUBLIC_URL + '/assets/nike-air-max.jpg',
    },
    {
        id: 3,
        name: 'Sony WH-1000XM4',
        category: 'Electronics',
        price: 299.99,
        image: process.env.PUBLIC_URL + '/assets/sony-wh1000xm4.jpg',
    },
    {
        id: 4,
        name: 'Apple AirPods Pro',
        category: 'Electronics',
        price: 249.99,
        image: process.env.PUBLIC_URL + '/assets/airpods.jpg',
    },
    {
        id: 5,
        name: 'Adidas Ultraboost',
        category: 'Clothing',
        price: 180.0,
        image: process.env.PUBLIC_URL + '/assets/adidas-ultraboost.jpg',
    },
    {
        id: 6,
        name: 'Apple Watch Series 8',
        category: 'Electronics',
        price: 399.99,
        image: process.env.PUBLIC_URL + '/assets/apple-watch.jpg',
    },
];

function App() {
    const [cartItems, setCartItems] = useState([]);
    const [cartOpen, setCartOpen] = useState(false);

    const addToCart = (product) => setCartItems([...cartItems, product]);

    const removeFromCart = (productToRemove) =>
        setCartItems(cartItems.filter((item) => item.id !== productToRemove.id));

    const toggleCart = () => setCartOpen(!cartOpen);

    return (
        <div className="App">
            <header className="navbar">
                <h1 className="logo">My Store</h1>
                <div className="cart" onClick={toggleCart}>
                    ðŸ›’ Cart ({cartItems.length})
                </div>
            </header>

            {cartOpen && (
                <div className="cart-dropdown">
                    {cartItems.length > 0 ? (
                        <ul>
                            {cartItems.map((item, index) => (
                                <li key={index}>
                                    <span>{item.name} - ${item.price}</span>
                                    <button onClick={() => removeFromCart(item)}>Remove</button>
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p>Your cart is empty.</p>
                    )}
                </div>
            )}

            <div className="content">
                <main className="product-grid">
                    {productData.map((product) => (
                        <Product key={product.id} product={product} addToCart={addToCart} />
                    ))}
                </main>
            </div>

            <footer className="footer">
                <p>&copy; 2024 My Store. All rights reserved.</p>
            </footer>
        </div>
    );
}

export default App;
