import React, { useState } from 'react';
import Product from './components/Product';
import './App.css';

const productData = Array.from({ length: 15 }, (_, i) => ({
    id: i + 1,
    name: `Product ${i + 1}`,
    category: i % 2 === 0 ? 'Clothing' : 'Electronics',
    price: (Math.random() * 100).toFixed(2),
    image: `https://via.placeholder.com/300x200?text=Product+${i + 1}`,
}));

function App() {
    const [cartItems, setCartItems] = useState([]);
    const [cartOpen, setCartOpen] = useState(false);

    const addToCart = (product) => {
        setCartItems((prevItems) => [...prevItems, product]);
    };

    const removeFromCart = (productToRemove) => {
        setCartItems((prevItems) =>
            prevItems.filter((product) => product.id !== productToRemove.id)
        );
    };

    const toggleCart = () => {
        setCartOpen(!cartOpen);
    };

    return (
        <div className="App">
            <header className="navbar">
                <h1 className="logo">My Store</h1>
                <div className="cart" onClick={toggleCart}>
                    ðŸ›’ Cart ({cartItems.length})
                </div>
            </header>

            {cartOpen && (
                <div className="cart-dropdown">
                    {cartItems.length > 0 ? (
                        <ul>
                            {cartItems.map((item, index) => (
                                <li key={index}>
                                    <span>{item.name} - ${item.price}</span>
                                    <button onClick={() => removeFromCart(item)}>Remove</button>
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p>Your cart is empty.</p>
                    )}
                </div>
            )}

            <div className="content">
                <main className="product-grid">
                    {productData.map((product) => (
                        <Product key={product.id} product={product} addToCart={addToCart} />
                    ))}
                </main>
            </div>

            <footer className="footer">
                <p>&copy; 2024 My Store. All rights reserved.</p>
            </footer>
        </div>
    );
}

export default App;
