import React, { useState } from 'react';
import Product from './components/Product';
import './App.css';

const productData = [
    { id: 1, name: 'Nike Shoes', category: 'Clothing', price: 89.99, image: 'https://via.placeholder.com/300x200?text=Nike+Shoes' },
    { id: 2, name: 'Adidas Jacket', category: 'Clothing', price: 120.49, image: 'https://via.placeholder.com/300x200?text=Adidas+Jacket' },
    { id: 3, name: 'Apple Watch', category: 'Electronics', price: 199.99, image: 'https://via.placeholder.com/300x200?text=Apple+Watch' },
];

function App() {
    const [cartItems, setCartItems] = useState([]);

    const addToCart = (product) => setCartItems([...cartItems, product]);

    return (
        <div className="App">
            <header className="navbar">
                <h1 className="logo">My Store</h1>
                <div className="cart">ðŸ›’ Cart ({cartItems.length})</div>
            </header>

            <div className="content">
                <main className="product-grid">
                    {productData.map((product) => (
                        <Product key={product.id} product={product} addToCart={addToCart} />
                    ))}
                </main>
            </div>
        </div>
    );
}

export default App;
