import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';

const products = [
    // Your existing products array
];

function App() {
    const [cartItems, setCartItems] = useState([]);

    const addToCart = (product) => {
        setCartItems((prevItems) => [...prevItems, product]);
    };

    const removeFromCart = (index) => {
        setCartItems((prevItems) => prevItems.filter((_, i) => i !== index));
    };

    const clearCart = () => {
        setCartItems([]);
    };

    return (
        <Router>
            <div className="App">
                <header className="navbar">
                    <div className="logo">Aman's Store</div>
                    <Link to="/cart" className="cart-icon">üõí ({cartItems.length})</Link>
                </header>

                <Routes>
                    <Route path="/" element={<Store addToCart={addToCart} />} />
                    <Route path="/cart" element={<CartPage cartItems={cartItems} removeFromCart={removeFromCart} clearCart={clearCart} />} />
                    <Route path="/checkout" element={<CheckoutPage cartItems={cartItems} />} />
                </Routes>

                <footer className="footer">¬© 2024 Aman's Store. All rights reserved.</footer>
            </div>
        </Router>
    );
}

function Store({ addToCart }) {
    return (
        <main className="content">
            <div className="product-grid">
                {products.map((product) => (
                    <div className="product-card" key={product.id}>
                        <img src={product.img} alt={product.name} />
                        <h3>{product.name}</h3>
                        <p>${product.price}</p>
                        <button onClick={() => addToCart(product)}>Add to Cart</button>
                    </div>
                ))}
            </div>
        </main>
    );
}

function CartPage({ cartItems, removeFromCart, clearCart }) {
    const totalPrice = cartItems.reduce((total, item) => total + item.price, 0);

    return (
        <div className="cart-page">
            <h2>Review Your Cart</h2>
            {cartItems.length === 0 ? (
                <p>Your cart is empty.</p>
            ) : (
                <>
                    <ul className="cart-list">
                        {cartItems.map((item, index) => (
                            <li key={index} className="cart-item">
                                <img src={item.img} alt={item.name} />
                                <span>{item.name}</span>
                                <span>${item.price}</span>
                                <button onClick={() => removeFromCart(index)}>Remove</button>
                            </li>
                        ))}
                    </ul>
                    <p>Total: ${totalPrice}</p>
                    <button onClick={clearCart} className="clear-cart-btn">Clear Cart</button>
                    <Link to="/checkout" className="checkout-btn">Proceed to Checkout</Link>
                </>
            )}
            <Link to="/" className="back-to-store-btn">‚Üê Back to Store</Link>
        </div>
    );
}

function CheckoutPage({ cartItems }) {
    const total = cartItems.reduce((sum, item) => sum + item.price, 0);
    const [formData, setFormData] = useState({ name: '', address: '', email: '' });
    const [orderComplete, setOrderComplete] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({ ...prevData, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setOrderComplete(true); // Mock order completion
    };

    if (orderComplete) {
        return (
            <div className="checkout-page">
                <h2>Order Complete!</h2>
                <p>Thank you for your purchase, {formData.name}!</p>
                <Link to="/" className="back-to-store-btn">Back to Store</Link>
            </div>
        );
    }

    return (
        <div className="checkout-page">
            <h2>Checkout</h2>
            <p>Total: ${total}</p>
            <form onSubmit={handleSubmit} className="checkout-form">
                <input type="text" name="name" placeholder="Full Name" value={formData.name} onChange={handleChange} required />
                <input type="text" name="address" placeholder="Shipping Address" value={formData.address} onChange={handleChange} required />
                <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
                <button type="submit" className="complete-order-btn">Complete Order</button>
            </form>
            <Link to="/cart" className="back-to-store-btn">‚Üê Back to Cart</Link>
        </div>
    );
}

export default App;
